import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from "@angular/forms";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  resValue: any;
  fromCurrency!: string;
  toCurrency!: string;
  amount!: number;
  exchangeRates: { [currency: string]: number } = {
    'USD': 1.126735,
    'GBP': 0.876893,
    'INR': 79.677056
  };

  constructor(public fb: FormBuilder) {
    this.CurrencyForm = this.fb.group({
      fromCurrency: ['USD'],
      amount: [''],
      toCurrency: ['USD']
    });
  }

  CurrencyForm: FormGroup;

  onSubmitt() {
    this.amount = +this.CurrencyForm.get('amount')?.value;
    this.fromCurrency = this.CurrencyForm.get('fromCurrency')?.value;
    this.toCurrency = this.CurrencyForm.get('toCurrency')?.value;

    const exchangeRateFrom = this.exchangeRates[this.fromCurrency];
    const exchangeRateTo = this.exchangeRates[this.toCurrency];

    if (exchangeRateFrom !== undefined && exchangeRateTo !== undefined) {
      if (this.fromCurrency === 'USD') {
        this.resValue = (this.amount * exchangeRateTo).toFixed(2);
      } else if (this.toCurrency === 'USD') {
        this.resValue = (this.amount / exchangeRateFrom).toFixed(2);
      } else {
        this.resValue = ((this.amount * exchangeRateTo) / exchangeRateFrom).toFixed(2);
      }
    } else {
      this.resValue = 'Invalid conversion';
    }
  }
}
<form [formGroup]="CurrencyForm" (ngSubmit)="onSubmitt()">
  Source CCY
  <select formControlName="fromCurrency" id="fromCurrency">
    <option value="USD">USD</option>
    <option value="GBP">GBP</option>
    <option value="INR">INR</option>
  </select>
  <br><br>
  Amount
  <input type="number" formControlName="amount" id="amount">
  <br><br>
  Target CCY
  <select formControlName="toCurrency" id="toCurrency">
    <option value="USD">USD</option>
    <option value="GBP">GBP</option>
    <option value="INR">INR</option>
  </select>
  <br><br>
  <button type="submit" id="submitButton">Submit</button>
  <br><br>
  Output
  <span id="result">Result: {{ resValue }}</span>
</form>
